import { Link } from "react-router-dom";
import { Header } from "../components/Header";
import { useState } from "react";

export function Create() {
  const [tripName, setTripName] = useState("");
  const [tripId, setTripId] = useState();
  async function createNewTrip() {
    const response = await fetch("http://localhost:5000/newTrip", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ tripName: tripName }),
    });

    const resData = await response.json();

    if (!response.ok) {
      alert(resData.message);
    } else {
      setTripId(resData.tripId);
    }
  }

  return (
    <div>
      <Header />
      <form className="logincon">
        <input
          type="text"
          placeholder="Enter trip name"
          value={tripName}
          onChange={(e) => setTripName(e.target.value)}
        />
        <div className="loginbtn">
          <Link to={`/components/main/${tripId}`}>
            <button onClick={createNewTrip}>Create</button>
          </Link>
          <Link to={"/"}>
            <button>Back</button>
          </Link>
        </div>
      </form>
    </div>
  );
}
